import React, { Component } from 'react'

class StudentShowReflection extends Component {
  state = {
    response: '',
    editing: true
  }

  componentDidMount(){
    const { progression } = this.props
    const edit = progression.question1Answer ? false : true
    this.setState({
      ...this.state,
      response: progression.question1Answer,
      editing: edit
    })
  }

  handleTextChange = e => {
    this.setState({
      ...this.state,
      response: e.target.value
    })
  }

  handleSubmit = e => {
    e.preventDefault()
    const { progression, currentUser, updateStudentProgression } = this.props
    updateStudentProgression(currentUser, progression, this.state)
    this.setState({
      ...this.state,
      editing: false
    })
  }

  handleEditClick = e => {
    this.setState({
      ...this.state,
      editing: true
    })
  }

  renderForm = () => {
    return (
      <div className="reflection-response">
        <textarea
          required
          onChange={this.handleTextChange}
          value={this.state.response}
          placeholder="Enter your response here">
        </textarea>
        <div className="submit-progression">
          <input
            type="submit"
            value="Save Response" />
        </div>
      </div>
    )
  }


  render(){
    const { reflection, student, progression } = this.props
    return (
      <form onSubmit={this.handleSubmit}>
      <div className="myprogression-reflection">
          <div className="lined-paper">
            <div className="myprogression-reflection-title">
              {reflection.title}
            </div>
            <div className="myprogression-reflection-question">
              <div className="reflection-response">
                <h3>{student.firstName}'s Response:</h3>
                <p>{progression.question1Answer}</p>
              </div>
            </div>
          </div>


      </div>
      </form>
    )
  }
}



export default StudentShowReflection
