import { combineReducers } from 'redux'

const klassReducer = combineReducers({
  byId: klassesById,
  allIds: allKlasses
})

export default function klassesById(
  state = { klasses: { byId: {}, allIds: [] }, requesting: false }, action) {

  switch(action.type) {
    case 'START_ADDING_KLASSES_REQUEST':
      return {
        ...state,
        klasses: [...state.klasses],
        requesting: true
      }

    case 'ADD_KLASSES':
      return {
        ...state,
        klasses: action.klasses,
        requesting: false
      }

    default:
      return state;
  }
}
