import { combineReducers } from 'redux'

const klassReducer = combineReducers({
  byId: klassesById,
  allIds: allKlasses
})

export default klassReducer

function klassesById(state = {}, action) {

  switch(action.type) {
    case 'START_ADDING_KLASSES_REQUEST':
      return {
        ...state
      }

    case 'ADD_KLASSES':
      return {
        ...action.klasses
      }

    default:
      return state;
  }
}

function allIds(state = [], action) {

  switch(action.type) {
    default:
      return state;
  }
}
