function currentUserReducer(state = {
  user: null,
  verified: false
}, action) {

  switch(action.type) {
    case 'SET_CURRENT_USER':
      return {
        ...this.state,
        user: action.user,
        verified: true
      }

    case 'CLEAR_CURRENT_USER':
      return {
        user: null,
        verified: true
      }

    default:
      return {
        ...this.state
      }
  }
}

export default currentUserReducer
