import React, { Component } from 'react';

class VimeoSearchContainer extends Component {
  state = {
    videos: [],
    searchTerm: "",
    videoIndex: ''
  }

  handleChange = (event) => {
    this.setState({
      videos: this.state.videos,
      searchTerm: event.target.value
    })
  }

  handleSubmit = (event) => {
    event.preventDefault()
    fetch(`/videos/getVimeoVideoMetadata/?q=${this.state.searchTerm}`)
      .then(resp => resp.json())
      .then(json => {
        this.setState({
          videos: this.createVideoObjects(json.data),
          searchTerm: ""
        })
      })
  }

  componentDidUpdate(){
    console.log(this.state.videos)
  }

  createVideoObjects = (videos) => {
    debugger
    return videos.map(video => {
      const url = 'http://www.youtube.com/embed/' + video.id.videoId
      return {
        title: this.formatTitle(video.snippet.title),
        videoId: video.id.videoId,
        channelTitle: video.snippet.channelTitle,
        description: video.snippet.description,
        date: this.formatDate(video.snippet.publishedAt),
        thumbnailUrl: video.snippet.thumbnails.medium.url,
        url: url
      }
    })
  }


  render() {
    return (
      <div className="searched-videos-display">
        <form onSubmit={this.handleSubmit}>
          <input
            type="text"
            value={this.state.searchTerm}
            onChange={this.handleChange}
            />
          <input type="submit" value="Search"/>
        </form>
        <div className="search-videos-container">
        </div>
      </div>
    )
  }

}

export default VimeoSearchContainer
