import React, { Component } from 'react'

class ProgressionItem extends Component {
  render(){
    const parser = new DOMParser()
    let title = parser.parseFromString('<!doctype html><body>' + this.this.props.video.snippet.title, 'text/html')
    title = title.body.textContent
    return (
      <div ref={node => this.props.innerRef(node)} className="progression-item-container">
        <div className="x-out" onClick={(event) => this.props.removeFromProgression(this.props.video)}>x</div>
        <div className="progression-item" onClick={event => this.props.handleProgressionItemClick(this.props.index)}>
          <img width="180px" src={this.props.video.snippet.thumbnails.medium.url} alt="learning video" />
          <br/>
          <div className="progression-item-title">{title}</div>
        </div>
      </div>
    )
  }
}

export default ProgressionItem
