import React, { Component } from 'react'
import StudentProgression from '../StudentProgression'
import '../css/student-summary.css'

class StudentShowSummary extends Component {
  renderProgressionRow = (progression) => {
    return (
      <StudentProgression progression={progression}/>
    )
  }

  render(){
    const { student, progressions } = this.props
    return (
      <div className="student-summary-page index-page">
        <div className="header">
          <div>Progression</div>
          <div>Date</div>
          <div>Reflection Question</div>
          <div>Student Answer</div>
          <div>Teacher Comment</div>
        </div>
        {progressions.map(progression => {
          return this.renderProgressionRow(progression)
        })}
      </div>
    )
  }
}

export default StudentShowSummary
