export function addKlass(klass){
  return (dispatch) => {
    dispatch({type: 'START_ADDING_KLASS_REQUEST'})
    fetch(`/klasses`)
      .then(resp => resp.json())
      .then(klasses => dispatch({ type: 'ADD_KLASS', klasses }))
  }
}
