export function addStudentProgression(student, progression) {
  return (dispatch) => {
    dispatch({type: 'START_ADDING_STUDENT_PROGRESSION_REQUEST'})
    debugger
    const params = {
      student: {
        progressionId: progression.id
      }
    }
    fetch(`/klasses/${student.klass_id}/students/${student.id}`, {
      method: 'PATCH',
      body: JSON.stringify(params),
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(resp => resp.json())
      .then(json => console.log(json))
  }
}

//.then(progressions => dispatch({ type: 'ADD_STUDENT_PROGRESSION', progressions }))


fetch(`/progressions/${progression.progression.id}`, {
  method: 'PATCH',
  body: JSON.stringify(progression),
  headers: {
    'Content-Type': 'application/json'
  }
})
  .then(resp => resp.json())
  .then(progression => {
    dispatch({ type: 'EDIT_PROGRESSION', progression })
  })
