function currentUserReducer(state = null, action) {

  switch(action.type) {
    case 'SET_CURRENT_USER':
    debugger
      return action.user || "none"

    case 'CLEAR_CURRENT_USER':
      return null

    default:
      return state;
  }
}

export default currentUserReducer
