import React, { Component } from 'react'
import { Route, NavLink } from "react-router-dom";
import ShowKlassContainer from './ShowKlassContainer'
import NewKlassForm from './NewKlassForm'

class KlassesContainer extends Component {
  state = {
    klasses: [],
    id: "",
    name: ""
  }

  componentDidMount(){
    fetch(`/klasses`)
      .then(resp => resp.json())
      .then(json => {
        this.setState({
          klasses: [...json]
        })
      })
  }

  updateSelectedKlass = (klassId) => {
    this.setState({
      klasses: [...this.state.klasses],
      selectedKlassId: klassId
    })
  }

  updateKlasses = (klass) => {
    this.setState({
      ...this.state,
      klasses: [
        ...this.state.klasses,
        klass
      ]
    })
  }

  klassSelectDropdown = () => {
    return (
      <div className="progression-menu-bar">
        <ul>
          {this.state.klasses.map((klass, index) => (
            <li key={index} className={this.state.selectedKlassId === klass.id ? 'selected' : ''}>
              {<NavLink to={`/classes/${klass.id}`}>{klass.name}</NavLink>}
            </li>
          ))}
            <li>{<NavLink to={`/classes/new`}>New Class</NavLink>}</li>
        </ul>
      </div>
    )

  }

  render() {
    return (
      <div>
        {this.klassSelectDropdown()}
        <Route exact path={`${this.props.match.url}/new`} render={() => <NewKlassForm {...this.props} updateKlasses={this.updateKlasses} />} />
        <Route path={`${this.props.match.url}/:id`} render={(routerProps) => <ShowKlassContainer {...routerProps} updateSelectedKlass={this.updateSelectedKlass} />} key={Math.random()} />
      </div>
    )
  }

}

export default KlassesContainer
