export default function klassReducer(
  state = {klasses: [], requesting: false}, action) {

  switch(action.type) {
    case 'START_ADDING_KLASSES_REQUEST':
      return {
        ...state,
        klasses: [...state.klasses],
        requesting: true
      }

    case 'ADD_KLASSES':
      return {
        ...state,
        klasses: action.klasses,
        requesting: false
      }

    case 'START_ADDING_KLASS_REQUEST':
      return {
        ...state,
        klasses: [...state.klasses],
        requesting: true
      }

    case 'ADD_KLASS':
      console.log('Adding klass action!')
      return {
        ...state,
        klasses: state.klasses.concat(action.klass)
      }

      // addKlass = (klass) => {
      //   this.setState({
      //     ...this.state,
      //     klasses: [
      //       ...this.state.klasses,
      //       klass
      //     ]
      //   })
      // }

    default:
      return state;
  }
}
