import React, { Component } from 'react'
import { NavLink } from 'react-router-dom';
import './navbar.css'

class NavBar extends Component{
  constructor(){
    this.node = ''
  }

  state = {
    klassDropdown: false
  }

  componentDidMount(){
    document.addEventListener('mousedown', this.handleClick)
  }

  componentWillUnmount(){
    document.removeEventListener('mousedown', this.handleClick)
  }

  handleClick = (e) => {

    if (this.node.contains(e.target)) { return }

    if (this.state.klassDropdown){
      this.setState({
        ...this.state,
        klassDropdown: !this.state.klassDropdown
      })
    }
  }

  handleKlassDropdownClick = () => {
    this.setState({
      ...this.state,
      klassDropdown: !this.state.klassDropdown
    })
  }

  renderKlassDropdown = () => {
    const { klasses } = this.props
    return (
      <div className="klass-dropdown-menu" ref={node => this.node = node}>
        {klasses.allIds.map(klassId => {
          const klass = klasses.byId[klassId]
          return ( <NavLink to={`/classes/${klass.id}`}>Class {klass.name}</NavLink> )
        })}
      </div>
    )
  }

  render(){
    return (
      <div>
        <div className="navbar">
          <NavLink to="/">Home</NavLink>
          <div className="klass-dropdown" onClick={this.handleKlassDropdownClick}>
            Classes
          </div>
          <NavLink to="/progressions/new">New Progression</NavLink>
          <NavLink to="/progressions">All Progressions</NavLink>
        </div>
        <div className="navbar-dropdowns">
          {this.state.klassDropdown ? this.renderKlassDropdown() : ''}
        </div>
      </div>
    )
  }

}

export default NavBar
