function currentUserReducer(state = {}, action) {

  switch(action.type) {
    case 'SET_CURRENT_USER':
      return action.user

    case 'CLEAR_CURRENT_USER':
      return null

    default:
      return {

      };
  }
}

export default currentUserReducer
