import React, { Component } from 'react';
import './Progression.css';
import ProgressionItem from './ProgressionItem'
import { Draggable } from 'react-beautiful-dnd'

class Progression extends Component {
  constructor(){
    super()
    this.progItemRef = React.createRef()
  }

  setProgItemRef = (ref) => {
    this.progRef = ref
  }

  displayProgression = () => {
    return (
      this.props.currProgression.map((video, index) => {
        return (
          <Draggable draggableId={video.id.videoId} index={index}>
            {(provided) => {
              return (
                <ProgressionItem
                  {...provided.draggableProps}
                  {...provided.dragHandleProps}
                  innerRef={this.progItemRef}
                  setProgItemRef={this.setProgItemRef}
                  video={video}
                  index={index}
                  removeFromProgression={this.props.removeFromProgression}
                  handleProgressionItemClick={this.props.handleProgressionItemClick}
                />
            )}}
        </Draggable>
        )
      })
    )
  }

  render(){
    return (
      <div ref={node => this.props.innerProgRef(node)} className="progression" onDragOver={this.props.handleDragOver} onDragLeave={this.props.handleDragLeave} onDrop={this.props.handleOnDrop} >
        {this.displayProgression()}
      </div>
    )
  }
}

export default Progression
