import { combineReducers } from 'redux'

const studentReducer = combineReducers({
  byId: studentsById,
  allIds: allStudents
})

export default studentReducer

function studentsById(state = {}, action) {
  switch(action.type) {
    case 'START_ADDING_STUDENTS_REQUEST':
      return {
        ...state
      }

    case 'ADD_STUDENTS':
      const normalizedObject = {}
      action.students.forEach(student => {
        normalizedObject[`student${student.id}`] = student
      })

      return {
        ...normalizedObject
      }

    case 'START_ADDING_STUDENT_TO_KLASS_REQUEST':
      return {
        ...state
      }

    case 'ADD_STUDENT_TO_KLASS':
      debugger
      const newStudent = {[`student${action.student.id}`]: action.student  }
      return {
        ...state
      }

    // case 'START_REMOVING_STUDENT_FROM_KLASS_REQUEST':
    //   return {
    //     ...state,
    //     klasses: [...state.klasses],
    //     requesting: true
    //   }
    //
    // case 'REMOVE_STUDENT_FROM_KLASS':
    //   const newKlasses = [...state.klasses]
    //   const klassIndex = newKlasses.findIndex(klass => klass.id === action.student.klass_id)
    //   const studentIndex = newKlasses[klassIndex].students.findIndex(st => st.id === action.student.id)
    //   newKlasses[klassIndex].students.splice(studentIndex, 1)
    //
    //   return {
    //     ...state,
    //     klasses: [...newKlasses],
    //     requesting: false
    //   }

    default:
      return state
  }
}


function allStudents(state = [], action) {

  switch(action.type) {
    case 'ADD_STUDENTS':
      return [
        ...action.students.map(student => `student${student.id}`)
      ]

    default:
      return state
  }
}
