import React, { Component } from 'react'
import Student from './Student'
import './student.css'

class StudentContainer extends Component {

  sortedIds = (students) => {
    return students.allIds.sort((idA, idB) => {
      const studentA = students.byId[idA].firstName.toLowerCase()
      const studentB = students.byId[idB].firstName.toLowerCase()
      if (studentA > studentB) { return 1 }
      else if (studentA < studentB ) {return -1 }
      else {return 0}
    })
  }

  displayStudents = () => {
    const { students, studentProgressions, removeStudentFromKlass, handleDragOver, handleDragLeave, handleDragDrop } = this.props
    return this.sortedIds(students).map((stId, index) => {
      const student = students.byId[stId]
      const myProgressions = studentProgressions.allIds.filter(spId => {
        const studentProgression = studentProgressions.byId[spId]
        if (studentProgression.studentId === `student${student.id}`){
          return progressions.byId[studentProgression.progressionId]
        }
      })


      return (
        <Student
          key={index}
          student={student}
          progressions={myProgressions}
          handleDragOver={handleDragOver}
          handleDragLeave={handleDragLeave}
          handleDragDrop={handleDragDrop}
          removeStudentFromKlass={removeStudentFromKlass}/>
    )})
  }

  render(){
    return (
      <div className="student-agendas">
        {this.props.students ? this.displayStudents() : ''}
      </div>
    )
  }
}

export default StudentContainer
