import { combineReducers } from 'redux'

const studentReducer = combineReducers({
  byId: studentsById,
  allIds: allStudents
})

export default studentReducer

function studentsById(state = {}, action) {
  switch(action.type) {
    case 'START_ADDING_STUDENTS_REQUEST':
      debugger
      return {
        ...this.state
      }

    case 'ADD_STUDENTS':
      return {
        ...action.students
      }

    default:
      return state
  }
}


function allStudents(state = [], action) {

  switch(action.type) {
    default:
      return state
  }
}
