export function addStudentToKlass(klass, student){
  return (dispatch) => {
    dispatch({type: 'START_ADDING_KLASS_REQUEST'})
    fetch('/klasses', {
      method: "POST",
      body: JSON.stringify(klass),
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(resp => resp.json())
      .then(klass => {
        dispatch({ type: 'ADD_KLASS', klass })
      })
  }
}
