import React, { Component } from 'react'
import { Route } from "react-router-dom";
import ShowKlassContainer from './ShowKlassContainer'
import NewKlassForm from './NewKlassForm'

class KlassesContainer extends Component {
  state = {
    klasses: []
  }

  componentDidMount(){
    fetch(`/klasses`)
      .then(resp => resp.json())
      .then(json => {
        this.setState({
          klasses: [...json]
        })
      })
  }

  handleKlassClick = (id) => {
    console.log(id)
    this.props.history.push(`/classes/${id}`)
  }

  klassSelectDropdown = () => {
    return (
      <div className="progression-menu-bar">
        <ul>
          {this.state.klasses.map((klass, index) => (
            <li key={index} onClick={event => this.handleKlassClick(klass.id)}>{klass.name}</li>
          ))}
            <li onClick={event => this.handleKlassClick("new")}>New Class</li>
        </ul>
      </div>
    )
  }

  render() {
    return (
      <div>
        {this.klassSelectDropdown()}
        <Route exact path={`${this.props.match.url}`} component={NewKlassForm} />
        <Route path={`${this.props.match.url}/:id`} component={ShowKlassContainer} />
      </div>
    )
  }

}

export default KlassesContainer
