export function signup(teacherData, history){
  return (dispatch) => {
     fetch(`/teachers`, {
       method: 'POST',
       body: JSON.stringify(teacherData),
       headers: {
         'Content-Type': 'application/json'
       },
       credentials: "include"
    })
      .then(resp => resp.json())
      .then(teacher => {
        if (teacher.error){
          history.push('/login')
        } else {
          dispatch({ type: 'SET_CURRENT_USER', teacher })
          history.push('/classes')
        }
      })
      .catch(console.log)
  }
}
