function videoSearchReducer(state = {}, action) {
  switch(action.type) {

    case 'START_ADDING_VIDEOS_REQUEST':
      return {
        ...state
      }

    case 'ADD_VIMEO_VIDEOS':
      debugger
      return {
        ...createVideoObjects(action.videos.data)
      }

    default:
      return state
  }
}

export default videoSearchReducer


// this.setState({
