import React, { Component } from 'react';
import './Progression.css';
import ProgressionItem from './ProgressionItem'
import { Draggable } from 'react-beautiful-dnd'

class Progression extends Component {
  displayProgression = () => {
    return (
      this.props.currProgression.map((video, index) => {
        return (
          <Draggable draggableId={video.id.videoId} index={index}>
            {(provided) => (
              <ProgressionItem
                {...provided.draggableProps}
                {...provided.dragHandleProps}

                video={video}
                index={index}
                removeFromProgression={this.props.removeFromProgression}
                handleProgressionItemClick={this.props.handleProgressionItemClick}
              />
            )}
        </Draggable>
        )
      })
    )
  }

  render(){
    return (
      <div innerRef={this.props.innerRef} className="progression" onDragOver={this.props.handleDragOver} onDragLeave={this.props.handleDragLeave} onDrop={this.props.handleOnDrop} >
        {this.displayProgression()}
      </div>
    )
  }
}

export default Progression
